// MovieList.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAllMovies();
  }, []);

  const fetchAllMovies = async () => {
    try {
      const response = await fetch(
        "https://api.themoviedb.org/3/movie/157336?api_key=4d3f4072120efaba6e670ade0c2161c8"
      );
      const data = await response.json();

      setMovies([data]);
      setLoading(false);
    } catch (error) {
      // Handle error
      console.log(error);
    }
  };

  const deleteMovie = async (id) => {
    try {
      await fetch(`http://www.omdbapi.com/?i=${id}&apikey=5908eaa3`, {
        method: 'DELETE',
      });
      fetchAllMovies();
    } catch (error) {
      // Handle error
      console.log(error);
    }
  };

  return (
    <div>
      <h1>Movies</h1>
      {loading ? (
        <p>Loading movies...</p>
      ) : movies.length > 0 ? (
        <ul>
          {movies.map((movie) => (
            <li key={movie.id}>
              <Link to={`/movie/${movie.id}`}>
                <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
                {movie.title}
              </Link>
              <button onClick={() => deleteMovie(movie.id)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No movies found.</p>
      )}
    </div>
  );
}

export default MovieList;
