import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function NavBar() {
  const [showUpcomingDropdown, setShowUpcomingDropdown] = useState(false);
  const [upcomingMovies, setUpcomingMovies] = useState([]);
  const [showNewReleasesDropdown, setShowNewReleasesDropdown] = useState(false);
  const [newReleasesMovies, setNewReleasesMovies] = useState([]);

  return (
    <div className="top-bar">
      <button className="nav-button" onClick={() => setShowUpcomingDropdown(!showUpcomingDropdown)}>
        Upcoming
      </button>
      {showUpcomingDropdown && (
        <div className="dropdown-content">
          <ul>
            {upcomingMovies.map((movie) => (
              <li key={movie.id}>{movie.title}</li>
            ))}
          </ul>
        </div>
      )}
      <button className="nav-button" onClick={() => setShowNewReleasesDropdown(!showNewReleasesDropdown)}>
        New Releases
      </button>
      {showNewReleasesDropdown && (
        <div className="dropdown-content">
          <ul>
            {newReleasesMovies.map((movie) => (
              <li key={movie.id}>{movie.title}</li>
            ))}
          </ul>
        </div>
      )}
      <button className="nav-button">
        <Link to="/recently-searched">Recently Searched Movies</Link>
      </button>
    </div>
  );
}

export default NavBar;
