import React, { useState } from 'react';
import Autosuggest from 'react-autosuggest';
import './MovieMadness.css';

const renderSuggestionsContainer = ({ containerProps, children }) => {
  return (
    <div {...containerProps} className="suggestions-container">
      <div className="suggestions-list">{children}</div>
    </div>
  );
};

const MovieSearch = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showBackgroundImage, setShowBackgroundImage] = useState(true);
  const [selectedMovie, setSelectedMovie] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    setShowBackgroundImage(false); // Hide background image on search button press

    try {
      const API_KEY = 'ae57e943f23fd67a50f1579e4ed5e7c5';
      const response = await fetch(
        `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=${searchTerm}`
      );
      const data = await response.json();

      const newSuggestions = data.results.map((movie) => ({
        title: movie.title,
        poster_path: movie.poster_path,
      }));

      setSuggestions(newSuggestions);
      onSearch(searchTerm); // Call onSearch with the searchTerm
    } catch (error) {
      console.error('Error fetching suggestions:', error);
    }
  };

  const handleInputChange = (e, { newValue }) => {
    setSelectedMovie(null);
    setSearchTerm(newValue);
  };

  const handleSuggestionsFetchRequested = async ({ value }) => {
    try {
      const API_KEY = 'ae57e943f23fd67a50f1579e4ed5e7c5';
      const response = await fetch(
        `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=${value}`
      );
      const data = await response.json();

      const newSuggestions = data.results.map((movie) => ({
        title: movie.title,
        poster_path: movie.poster_path,
      }));

      setSuggestions(newSuggestions);
    } catch (error) {
      console.error('Error fetching suggestions:', error);
    }
  };

  const handleSuggestionSelected = (event, { suggestion }) => {
    setSelectedMovie(suggestion);
    onSearch(suggestion.title);
  };

  const getSuggestionValue = (suggestion) => suggestion.title;

  const inputProps = {
    placeholder: 'Enter a movie title',
    value: searchTerm,
    onChange: handleInputChange,
    disabled: selectedMovie !== null,
  };

  return (
    <div className={`center-search-bar ${showBackgroundImage ? 'show-image' : ''}`}>
      <form onSubmit={handleSearch} className="search-form">
        <div className="search-input-container">
          <Autosuggest
            suggestions={suggestions}
            onSuggestionsFetchRequested={handleSuggestionsFetchRequested}
            renderSuggestionsContainer={renderSuggestionsContainer}
            getSuggestionValue={getSuggestionValue}
            inputProps={inputProps}
            suggestionsContainerProps={{
              style: {
                position: 'absolute',
                maxHeight: '200px',
                overflowY: 'auto',
                zIndex: '2',
              },
            }}
            onSuggestionSelected={handleSuggestionSelected}
            renderSuggestion={(suggestion) => (
              <div className="suggestion-item">
                <img
                  className="suggestion-image"
                  src={`https://image.tmdb.org/t/p/w200${suggestion.poster_path}`}
                  alt={suggestion.title}
                />
                <span className="suggestion-title">{suggestion.title}</span>
              </div>
            )}
          />
        </div>
        <button type="submit" className="search-button">
          Search ðŸ”Ž
        </button>
      </form>
      {!showBackgroundImage && (
        <div className="search-results">
          {/* Render the search results here */}
        </div>
      )}
    </div>
  );

};

export default MovieSearch;
