import React, { useState } from 'react';
import Autosuggest from 'react-autosuggest';
import './MovieMadness.css';

const renderSuggestionsContainer = ({ containerProps, children }) => {
  return (
    <div {...containerProps} className="suggestions-container">
      <div className="suggestions-list">
        {children}
      </div>
    </div>
  );
};

const MovieSearch = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  const handleInputChange = (e, { newValue }) => {
    setSearchTerm(newValue);
  };

  const handleSuggestionsFetchRequested = async ({ value }) => {
    try {
      const API_KEY = 'ae57e943f23fd67a50f1579e4ed5e7c5';
      const response = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=${value}`);
      const data = await response.json();

      const newSuggestions = data.results.map((movie) => ({
        title: movie.title,
        poster_path: movie.poster_path,
      }));

      setSuggestions(newSuggestions);
    } catch (error) {
      console.error('Error fetching suggestions:', error);
    }
  };

  const getSuggestionValue = (suggestion) => suggestion.title;

  const inputProps = {
    placeholder: 'Enter a movie title',
    value: searchTerm,
    onChange: handleInputChange,
  };

  return (
    <div className="search-bar"> {/* Added CSS class for centering */}
      <form onSubmit={handleSearch}>
        <Autosuggest
          suggestions={suggestions}
          onSuggestionsFetchRequested={handleSuggestionsFetchRequested}
          renderSuggestionsContainer={renderSuggestionsContainer}
          getSuggestionValue={getSuggestionValue}
          inputProps={inputProps}
          suggestionsContainerProps={{
            style: {
              position: 'absolute',
              maxHeight: '200px',
              overflowY: 'auto',
              zIndex: '2',
            },
          }}
          renderSuggestion={(suggestion) => (
            <div className="suggestion-item">
              <img
                className="suggestion-image"
                src={`https://image.tmdb.org/t/p/w200${suggestion.poster_path}`}
                alt={suggestion.title}
              />
              <span className="suggestion-title">{suggestion.title}</span>
            </div>
          )}
        />
        <button type="submit">SearchðŸ”Ž</button>
      </form>
    </div>
  );
};

export default MovieSearch;
