// MovieList.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = () => {
    fetch('http://www.omdbapi.com/?s=movie&apikey=5908eaa3')
      .then((response) => response.json())
      .then((data) => {
        console.log(data.Search);
        const moviesWithPosters = data.Search.map((movie) => {
          return fetch(`http://www.omdbapi.com/?i=${movie.imdbID}&apikey=5908eaa3`)
            .then((response) => response.json())
            .then((movieData) => {
              return {
                ...movie,
                Poster: movieData.Poster,
              };
            });
        });

        Promise.all(moviesWithPosters)
          .then((moviesWithUpdatedPosters) => {
            setMovies(moviesWithUpdatedPosters || []);
            setLoading(false);
          });
      });
  };

  const deleteMovie = (id) => {
    fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=5908eaa3/${id}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then(() => {
        fetchMovies();
      });
  };

  return (
    <div>
      <h1>Movies</h1>
      {loading ? (
        <p>Loading movies...</p>
      ) : movies.length > 0 ? (
        <ul>
          {movies.map((movie) => (
            <li key={movie.imdbID}>
              <Link to={`/movie/${movie.imdbID}`}>
                <img src={movie.Poster} alt={movie.Title} />
                {movie.Title}
              </Link>
              <button onClick={() => deleteMovie(movie.imdbID)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No movies found.</p>
      )}
    </div>
  );

}

export default MovieList;
