// MovieList.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAllMovies();
  }, []);

  const fetchAllMovies = async () => {
    let allMovies = [];
    let page = 1;
    let totalPages = 0;
    const perPage = 10;

    do {
      const response = await fetch(`http://www.omdbapi.com/?s=movie&apikey=5908eaa3&page=${page}`);
      const data = await response.json();

      allMovies = [...allMovies, ...data.Search];
      totalPages = Math.ceil(data.totalResults / perPage);
      page++;
    } while (page <= totalPages);

    const moviesWithPosters = await Promise.all(
      allMovies.map(async (movie) => {
        const response = await fetch(`http://www.omdbapi.com/?i=${movie.imdbID}&apikey=5908eaa3`);
        const movieData = await response.json();

        return {
          ...movie,
          Poster: movieData.Poster,
        };
      })
    );

    setMovies(moviesWithPosters);
    setLoading(false);
  };

  const deleteMovie = async (id) => {
    await fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=5908eaa3/${id}`, {
      method: 'DELETE',
    });
    fetchAllMovies();
  };

  return (
    <div>
      <h1>Movies</h1>
      {loading ? (
        <p>Loading movies...</p>
      ) : movies.length > 0 ? (
        <ul>
          {movies.map((movie) => (
            <li key={movie.imdbID}>
              <Link to={`/movie/${movie.imdbID}`}>
                <img src={movie.Poster} alt={movie.Title} />
                {movie.Title}
              </Link>
              <button onClick={() => deleteMovie(movie.imdbID)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No movies found.</p>
      )}
    </div>
  );
}

export default MovieList;
